mapper.select:
SELECT tb_user.`nick_name`,tb_comment.`create_time`, tb_sku.`skuvalue_id`,tb_sku.`sku_id`, tb_comment.`content`,tb_comment.`comment_id`,tbi.`image_path`,tb_comment.`spare_one`,tb_product.`product_name`,tb_comment.`review`,tb_comment.`reply_conte` FROM tb_comment INNER JOIN tb_sku ON tb_sku.`sku_id`=tb_comment.`sku_id` INNER JOIN tb_product ON tb_sku.`product_id`=tb_product.`product_id` INNER JOIN tb_user ON tb_user.`user_id`=tb_comment.`user_id` LEFT JOIN (SELECT * FROM tb_image WHERE `image_type`=1) tbi ON tbi.`product_id`=tb_comment.`comment_id` WHERE 1=1
and tb_comment.`spare_one`=#{spareOne}
and tb_sku.`product_id`=#{productId}
ORDER BY create_time DESC

SELECT COUNT(tb_comment.comment_id) FROM tb_comment
INNER JOIN tb_sku ON tb_sku.`sku_id`=tb_comment.`sku_id`
WHERE tb_sku.`product_id`=#{id}
GROUP BY tb_comment.spare_one;
