mapper.select:
SELECT tb_image.`image_id`,tb_image.`image_path`,tb_product.`product_id`,tb_product.`product_name`, sku.`sku_id`,sku.present_price FROM tb_product LEFT JOIN ( SELECT * FROM tb_image WHERE image_type=0 GROUP BY tb_image.`product_id`) AS tb_image ON tb_image.`product_id`=tb_product.`product_id` LEFT JOIN (SELECT * FROM tb_sku ORDER BY `present_price`) AS sku ON sku.`product_id`=tb_product.`product_id` WHERE 1=1
and tb_product.weight=#{weight}
AND tb_product.`category_one`=#{categoryOne}
GROUP BY tb_product.`product_id`

SELECT s.sku_id,s.product_id,s.stock,s.original_price,s.present_price, p.product_id,p.product_name,p.product_desc,p.product_details,p.sales,p.longegral, v.skuvalue_id,v.skuvalue_value FROM tb_sku s,tb_product p,tb_skuvalue v WHERE s.`product_id`=p.`product_id` AND s.`skuvalue_id`=v.`skuvalue_id`
AND s.product_id=#{productId}
AND s.sku_id=#{skuId}

SELECT tbpr.product_id, tbpr.product_name, tbpr.sales, tbpr.spare_one, tbpr.status, tbpr.create_time, tbpr.down_time, tbsku.present_price, sumsku.countid, tbimg.image_path FROM tb_product tbpr LEFT JOIN tb_Image tbimg ON tbpr.product_id = tbimg.product_id LEFT JOIN (SELECT sku.product_id,sku.present_price FROM (SELECT * FROM tb_sku ORDER BY present_price) sku GROUP BY product_id) tbsku ON tbsku.product_id = tbpr.product_id LEFT JOIN (SELECT tb_sku.product_id,SUM(tb_sku.stock) countid FROM tb_sku GROUP BY tb_sku.product_id) sumsku ON sumsku.product_id=tbpr.product_id WHERE 1=1
AND tbpr.status=#{status}
AND tbpr.product_name LIKE CONCAT ('%',#{product_name},'%') GROUP BY tbpr.product_id
