mapper.select:
SELECT tb_image.`image_id`,tb_image.`image_path`,tb_product.`product_id`,tb_product.`product_name`, sku.`sku_id`,sku.present_price FROM tb_product LEFT JOIN ( SELECT * FROM tb_image WHERE image_type=0 GROUP BY tb_image.`product_id`) AS tb_image ON tb_image.`product_id`=tb_product.`product_id` LEFT JOIN (SELECT * FROM tb_sku ORDER BY `present_price`) AS sku ON sku.`product_id`=tb_product.`product_id` WHERE 1=1
and tb_product.weight=#{weight}
AND tb_product.`category_one`=#{categoryOne}
GROUP BY tb_product.`product_id`
